package main.java;/** * class for matrix positions */import java.lang.*;  // for class Exceptionpublic class InvalidOperationException extends RuntimeException {            /**         * Constructs a <code>InvalidOperationException</code>          * with no detail message.         */        public InvalidOperationException() {                super();        }        /**         * Constructs a <code>DimensionException</code> with the specified          * detail message.          *         * @param message A string value that contains the detailed message to relay.         */        public InvalidOperationException(String message) {                super( message );        }}